package patternMatcher;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class JavaPattern {
	private static final Pattern REQUEST_VERSION_PATTERN =
			Pattern.compile("^WLTPRequestHead\\s+\\d+\\s+\\d+\\s+wltpservice\\s+([vV]\\d+)\\s.*");
	
	public final static String message1 = "WLTPRequestHead                 4         48        wltpservice                                     v2                                              me                                              ffc7e0f4-8626-4fe7-a574-6c1178b8eb34            WLTPVehicleProperties           7         8         20161114X0A     12      0       2016    8VAB6L  S9S9    WLTPVehiclePRNo                 3         7         MAAUE0AMABR1PAMAED7LJWLTPVehicleSNo                  2         3         SA5S99WLTPVehicleDif                  0         26        ";

	public final static String message2 = "WLTPRequestHead                 4         48        wltpservice                                     v4                                              ICTO-6749                                       106660b9-cfe0-41d2-b1da-f9e36437f70b            "
+"WLTPVehicleProperties           7         8         20160930X0A     901     DEU     0       2016    8V      8V1A6C  A1A1"+    
"WLTPVehiclePRNo                 7         189       "+
"WLTPVehicleYNo                  3         2         Y99YAA"+
"WLTPVehicleSNo                  3         2         S99SA5"+
"WLTPVehicleDif                  26        1         ABS_CW-0.5";

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Matcher matcher1 = REQUEST_VERSION_PATTERN.matcher(message1);
		Matcher matcher2 = REQUEST_VERSION_PATTERN.matcher(message2);
		
		System.out.println("MATCHER1 : " + matcher1.matches());
		System.out.println("MATCHER2 : " + matcher2.matches());

	}

}
